
public class Traingle6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		for(int i=1; i<=n; i++)
		{
		 
		    for(int sp=(n-i); sp>=1; sp--)
		    {
		       System.out.print("  ");
		     }

		  for(int k=i; k>=1;k--)
		  {
		      System.out.print(k+" "); 
		  }
		System.out.println();
		  
		}
		
		
		
	}
	

}
